mapper.select:
SELECT boardid, title, content, writer, regDate, viewCnt FROM shop_board
where title like concat('%',#{keyword}, '%')
order by boardid desc limit #{skip}, #{amount}

mapper.update:
UPDATE shop_board SET
viewCnt = viewCnt+1
WHERE boardid = #{boardid}
